import Head from 'next/head';
import list from './data';

export default function Home() {
  const data = list.sort((a, b) => {
    const abbrA = a.abbr.toLowerCase();
    const abbrB = b.abbr.toLowerCase();

    if (abbrA > abbrB) {
      return 1;
    } else if (abbrA < abbrB) {
      return -1;
    }

    return 0;
  });

  return (
    <div className="container">
      <Head>
        <title>Abbreviation's for people who is working from home (WFH)</title>
        <link rel="icon" href="/favicon.ico" />
      </Head>

      <main>
        <div className="search">
          <input type="text" placeholder="Search for abbreviation's here..." autoFocus />
        </div>
        <div className="search-data">
          <ul>
            {data.map((datum) => {
              return (
                <li key={datum.abbr}>
                  <div className="search-data-left">{datum.desc}</div>
                  <div className="search-data-right">{datum.abbr}</div>
                </li>
              );
            })}
          </ul>
        </div>
      </main>

      <footer>
        <a href="https://github.com/gokulkrishh/wfh-abbreviations" target="_blank" rel="noopener noreferrer">
          Send a pr if you know one
        </a>
      </footer>

      <style jsx>{`
        .container {
          min-height: 100vh;
          padding: 0 0.5rem;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        main {
          padding: 5rem 0;
          flex: 1;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        footer {
          width: 100%;
          height: 45px;
          border-top: 2px solid var(--secondary-bg-color);
          display: flex;
          justify-content: center;
          align-items: center;
        }

        footer a {
          display: flex;
          justify-content: center;
          align-items: center;
        }

        a {
          color: inherit;
        }

        ul {
          list-style-type: none;
          display: flex;
          flex-direction: column;
          width: 455px;
          padding: 0;
        }

        li {
          display: flex;
          justify-content: space-between;
          margin-bottom: 20px;
        }

        .title,
        .description {
          text-align: center;
        }

        .description {
          line-height: 1.5;
          font-size: 1.5rem;
        }

        code {
          background: #fafafa;
          border-radius: 5px;
          padding: 0.75rem;
          font-size: 1.1rem;
          font-family: Menlo, Monaco, Lucida Console, Liberation Mono, DejaVu Sans Mono, Bitstream Vera Sans Mono, Courier New, monospace;
        }

        .grid {
          display: flex;
          align-items: center;
          justify-content: center;
          flex-wrap: wrap;

          max-width: 800px;
          margin-top: 3rem;
        }

        .card {
          margin: 1rem;
          flex-basis: 45%;
          padding: 1.5rem;
          text-align: left;
          color: inherit;
          text-decoration: none;
          border: 1px solid #eaeaea;
          border-radius: 10px;
          transition: color 0.15s ease, border-color 0.15s ease;
        }

        .card:hover,
        .card:focus,
        .card:active {
          color: #0070f3;
          border-color: #0070f3;
        }

        .card h3 {
          margin: 0 0 1rem 0;
          font-size: 1.5rem;
        }

        .card p {
          margin: 0;
          font-size: 1.25rem;
          line-height: 1.5;
        }

        .logo {
          height: 1em;
        }

        input {
          width: 460px;
          height: 56px;
          padding: 0 15px;
          font-size: 18px;
          font-weight: normal;
          border-radius: 4px;
          box-shadow: none;
          border: 1px solid transparent;
          background-color: var(--secondary-bg-color);
          color: var(--primary-text-color);
        }

        @media (max-width: 600px) {
          .grid {
            width: 100%;
            flex-direction: column;
          }
        }
      `}</style>

      <style jsx global>{`
        :root {
          --primary-bg-color: #121212;
          --secondary-bg-color: #1f1f1f;
          --primary-text-color: #fff;
          --secondary-text-color: #7e7e7e;
        }

        html,
        body {
          padding: 0;
          margin: 0;
          font-family: -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Oxygen, Ubuntu, Cantarell, Fira Sans, Droid Sans, Helvetica Neue,
            sans-serif;
          background-color: var(--primary-bg-color);
          color: var(--primary-text-color);
        }

        * {
          box-sizing: border-box;
        }
      `}</style>
    </div>
  );
}
